def damagetake():
    global health, playerx, playery, enemyx, enemyy
    display.text(str("Attack"), int(0), int(0), 0)
    if playerx-enemyx < 25 and playerx-enemyx > -25:
        if playery-enemyy < 25 and playery-enemyy > -25:
    
            health -= round(3*random.random())
                                    
            time.sleep(0.1)
    return health
def enemymovement():
    global playerx, playery, enemyx, enemyy
    if playerx > enemyx:
        enemyx += 1
    elif playerx < enemyx:
        enemyx -= 1
    if playery > enemyy:
        enemyy += 1
    if enemyy > playery:
        enemyy -= 1
    time.sleep(0.13)
    return enemyy, enemyx

